import React, { useState } from "react";

const Login = ({ onLogin }) => {
  // Definir el estado para el nombre de usuario y la contraseña
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:3000/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (response.ok) {
        // Pasa el token al componente principal
        onLogin(data.token);
      } else {
        alert(data.message); // Muestra un mensaje de error si el login falla
      }
    } catch (error) {
      console.error("Error al autenticar:", error);
      alert("Hubo un error al intentar iniciar sesión.");
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)} // Actualiza el estado de 'username'
            placeholder="Introduce tu nombre de usuario"
          />
        </div>

        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)} // Actualiza el estado de 'password'
            placeholder="Introduce tu contraseña"
          />
        </div>

        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
